import { updateProfile } from 'firebase/auth'
import { doc, updateDoc } from 'firebase/firestore'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'
import React, { useContext, useEffect, useState } from 'react'
import foto from "../assets/Ã­ndice.png"
import { ChatContext } from '../context/Context'
import { db, logOut, storage } from '../firebase/firebase'
import { Oval } from  'react-loader-spinner'
import { Navigate, useNavigate } from 'react-router-dom'
const Navbar = ({chats, setChats}) => {
  const navigate = useNavigate()
  const {userInfo} = useContext(ChatContext)
  const {displayName, photoURL,dispatch, uid, setShowFriend} = userInfo
  const [img, setImg] = useState("")
  const [showLoader, setShowLoader] = useState(false)
  const handleChange = async(e) => {

    setShowLoader(true)
    const storageRef = ref(storage, uid);

  await uploadBytesResumable(storageRef, e.target.files[0]).then(() => {
    getDownloadURL(storageRef).then(async (downloadURL) => {
      try {
        //Update profile
        await updateProfile(userInfo, {
          
          photoURL: downloadURL,
        });
        //create user on firestore
        await updateDoc(doc(db, "users", uid), {
          
          photoURL: downloadURL,
        });
        setImg(downloadURL)
        setShowLoader(false)
      } catch (err) {
       
       
      }
    });
  })
  }
   
  const handleLogOut =async() => {

    await logOut()
    
  }
  return (
    <div className="navbar-container">
        {showLoader &&
        <Oval
        height={38}
        width={38}
        color="#4fa94d"
        wrapperStyle={{}}
        wrapperClass=""
        visible={true}
        ariaLabel='oval-loading'
        secondaryColor="#4fa94d"
        strokeWidth={2}
        strokeWidthSecondary={2}
      
      />
        }
        
        
        {!showLoader &&
        <>
          <input onChange={handleChange} type="file" id="photoupdate" hidden/>
          <label  className="photolabel" htmlFor='photoupdate'> 
          <img className="profilephoto" src={img === "" ? (photoURL !== null ? photoURL : "data:image/png;base64,") : img}/>
          </label>
        </>
        }
        
        
        <p className="name">{displayName}</p>
        <button type="button" className="logoutbutton" onClick={handleLogOut} >Log Out</button>
    </div>
  )
}

export default Navbar